import { createSlice } from "@reduxjs/toolkit";

const initialState = { items: [],quantity:0 };

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    xyzfn(state, action) {},
  },
});

export const cartActions = cartSlice.actions;

export default cartSlice.reducer;
