import { cartActions } from "./cart-slice";
import { uiActions } from "./ui-slice";

const FIREBASE_URL =
  "https://advancedredux-7db44-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json";

export const fetchData = () => {
  return async (dispatch) => {
    const retrieveData = async () => {
      try {
        const response = await fetch(FIREBASE_URL);
        if (!response.ok) {
          throw new Error(
            `Failed to fetch cart data: ${response.status} ${response.statusText}`
          );
        }
        const data = await response.json();
        dispatch(cartActions.replaceCart(data));
      } catch (error) {
        console.error("Error fetching data:", error); // Log the error
        dispatch(
          uiActions.showNotification({
            status: "error",
            title: "Error",
            message: error.message || "Failed to fetch data", // Display error message
          })
        );
      }
    };
    retrieveData();
  };
};

export const sendCartData = (cart) => {
  return async (dispatch) => {
    dispatch(
      uiActions.showNotification({
        status: "pending",
        title: "Sending...",
        message: "Sending cart data...",
      })
    );

    const httpsRequest = async () => {
      try {
        const response = await fetch(FIREBASE_URL, {
          method: "PUT",
          body: JSON.stringify(cart),
        });

        if (!response.ok) {
          throw new Error(
            `Failed to send cart data: ${response.status} ${response.statusText}`
          );
        }

        dispatch(
          uiActions.showNotification({
            status: "success",
            title: "Success!",
            message: "Sent data successfully!",
          })
        );
      } catch (error) {
        console.error("Error sending data:", error); // Log the error
        dispatch(
          uiActions.showNotification({
            status: "error",
            title: "Error",
            message: error.message || "Sending cart data failed", // Display error message
          })
        );
      }
    };
    httpsRequest();
  };
};
